  { topic: "The Wealth Transfer Happening Right Now", archetype: "urgency_narrative", visual_intent: "economic_shift", priority: 89 }
];

const VISUAL_DNA_MAP = {
  power_finance: ["skyscrapers", "vaults", "dark_boardrooms", "luxury_suits"],
  surveillance: ["cameras", "data_streams", "government_buildings", "documents"],
  corporate_power: ["wall_street", "bank_buildings", "money_stacks", "contracts"],
  deception: ["masks", "shadows", "fine_print", "manipulation"],
  wealth_building: ["compound_growth", "real_estate", "investment_charts", "passive_income"],
  financial_hack: ["credit_cards", "points", "travel", "rewards"],
  inflation_visual: ["shrinking_dollar", "price_tags", "grocery_costs", "fed_building"],
  human_behavior: ["brain_scans", "decision_making", "crowd_psychology", "emotions"],
  systems_control: ["chess_pieces", "puppet_strings", "class_divide", "policy_documents"],
  economic_shift: ["wealth_gap", "market_crashes", "opportunity_zones", "timing"]
};

function selectEliteTopic() {
  const totalWeight = ELITE_TOPIC_POOL.reduce((sum, t) => sum + t.priority, 0);
  let random = Math.random() * totalWeight;

  for (const topic of ELITE_TOPIC_POOL) {
    random -= topic.priority;
    if (random <= 0) {
      return {
        ...topic,
        visual_assets: VISUAL_DNA_MAP[topic.visual_intent] || [],
        selected_at: new Date().toISOString(),
        execution_id: $execution?.id || 'manual'
      };
    }
  }
  return { ...ELITE_TOPIC_POOL[0], visual_assets: VISUAL_DNA_MAP[ELITE_TOPIC_POOL[0].visual_intent] };
}

return [{ json: selectEliteTopic() }];

