const input = $input.all()[0]?.json || {};
const previousData = $node['ðŸ§  AAVE Brain - Topic Selector'].json || {};

const script = input.message?.content || input.text || '';

if (!script || script.length < 50) {
  throw new Error('SCRIPT_TOO_SHORT: Generated script is invalid or empty');
}

function optimizeForTTS(text) {
  return text
    .replace(/\n{2,}/g, ' ... ')
    .replace(/\*\*/g, '')
    .replace(/[#_]/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}

const optimizedScript = optimizeForTTS(script);
const wordCount = optimizedScript.split(/\s+/).length;
const estimatedDuration = Math.ceil(wordCount / 2.5);

return [{
  json: {
    topic: previousData.topic,
    archetype: previousData.archetype,
    visual_intent: previousData.visual_intent,
    visual_assets: previousData.visual_assets,
    script_raw: script,
    script_optimized: optimizedScript,
    word_count: wordCount,
    estimated_duration_seconds: estimatedDuration,
    content_type: estimatedDuration <= 60 ? 'SHORT' : 'LONGFORM',
    generation_timestamp: new Date().toISOString()
  }
}];

